package test;

public class StringExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "mahesh wa";
				String name1 = "waran s";
				
				System.out.println("no of char is : " +name1.length());
				System.out.println(name + name1);
				System.out.println("the 5th char is :" +name.charAt(4));
				System.out.println("the last char is: " +name.charAt(name.length()-1));
				System.out.println("the string is upprecase :" + name.toUpperCase());
				System.out.println("the strin is lowercase :" + name.toLowerCase());

	}

}
